/* css reset */

* {
    box-sizing: border-box;
}

*::before,
*::after {
    box-sizing: border-box;
}

* {
    margin: 0;
    padding: 0;
}

ul,
ol {
    list-style: none;
}

a {
    text-decoration: none;
    color: inherit;
}
/*index style */

body {
    background: #eee;
}

@media (min-width:500px) {
    .globalHeader {
        margin: 60px 0;
    }
}

.searchForm {
    margin: 20px;
    display: flex;
    justify-content: space-between;
}

@media screen,
(min-width:500px) {
    .searchForm {
        max-width: 400px;
        /* 当一个元素有宽度，最大最小宽度也算，就可以使用margin方法来居中 */
        /* 居中优先使用下面这种写法，不要用margin：0，auto */
        margin-left: auto;
        margin-right: auto;
    }
}

.searchForm>input {
    width: 100%;
    margin-right: 10px;
    height: 40px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    outline: none;
}

.searchForm>button {
    white-space: nowrap;
    padding: 0 10px;
    border: none;
    border-radius: 4px;
    background: #0282B9;
    color: white;
    padding: 0 28px;
    font-size: 16px;
}

.globalMain {
    max-width: 900px;
    margin-right: auto;
    margin-left: auto;
}

.siteList {
    margin: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}

@media (min-width:500px) {
    .siteList {
        margin-left: 0;
        /* 负margin可以让盒子变宽，就可以一行容纳5个盒子了 */
        margin-right: -25px;
        justify-content: flex-start;
    }
}

.siteList>li {
    margin-bottom: 10px;
}

@media (min-width:500px) {
    .siteList>li {
        margin-right: 25px;
    }
}

.siteList .site {
    width: 160px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background: white;
    border: 1px solid #ddd;
    border-radius: 4px;
    padding: 20px 0;
    position: relative;
    cursor: pointer;
}

.siteList .site>.logo {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
    /* 将盒子里面的字母变为大写 */
    text-transform: uppercase;
}

.siteList .site>.logo>img {
    max-width: 100%;
    max-height: 100%;
}

.siteList .site>.link {
    font-size: 14px;
    margin-top: 5px;
}

.siteList .site>.close {
    position: absolute;
    right: 5px;
    top: 5px;
    font-size: 26px;
    display: none;
}

.siteList .site:hover>.close {
    display: block;
    cursor: default;
}

.siteList .addButton {
    border: 1px solid #ddd;
    background: white;
    border-radius: 4px;
    width: 160px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 20px 0;
}

.siteList .addButton .icon {
    width: 56px;
    height: 56px;
}

.siteList .addButton .icon-wrapper {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
}

.siteList .addButton .text {
    font-size: 14px;
    margin-top: 4px;
}
